import { supabase } from "../supabase";

type Inputs = {
  email: string;
  password: string;
  address?: string;
};

const signUp = async ({ email, password }: Inputs) => {
  const response = await supabase.auth.signUp({
    email,
    password,
  });

  return response;
};

// 유저 데이터
const getUserInfo = async () => {
  const { data, error } = await supabase.from("user_info").select("*");

  return data;
};

export { signUp, getUserInfo };
