import dayjs from "dayjs";

type CalculateTimeUntilReturn = { message: string; isOver: boolean };

export function transDate(date: Date | string) {
  return dayjs(date).format("YYYY. MM. DD");
}

export function calculateTimeUntil(
  targetDate: Date | string
): CalculateTimeUntilReturn {
  const result = {
    message: "",
    isOver: false,
  };

  const now = dayjs();
  const endDate = dayjs(targetDate);

  if (endDate.isBefore(now)) {
    result.isOver = true;
    result.message = "목표 날짜가 이미 지났습니다.";
    return result;
  }

  const diff = endDate.diff(now);

  const days = Math.floor(diff / (24 * 60 * 60 * 1000));
  const hours = Math.floor((diff % (24 * 60 * 60 * 1000)) / (60 * 60 * 1000));
  const minutes = Math.floor((diff % (60 * 60 * 1000)) / (60 * 1000));
  const seconds = Math.floor((diff % (60 * 1000)) / 1000);

  result.message = `${days}일 ${hours}시간 ${minutes}분 ${seconds}초 남음`;
  result.isOver = true;

  return result;
}
