import { styled } from "styled-components";

type Props = {
  auctionOver: boolean;
  auctionTimeStamp: string | undefined;
};

const DetailTimeStamp = ({ auctionOver, auctionTimeStamp }: Props) => {
  return (
    <StTimeStampWrapper>
      {auctionOver && (
        <div>
          <h2>경매가 종료되었습니다.</h2>
        </div>
      )}
      {!auctionOver && (
        <div>
          <h2>경매 남은시간</h2>
          <span>{auctionTimeStamp}</span>
        </div>
      )}
    </StTimeStampWrapper>
  );
};

const StTimeStampWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

  font-weight: bold;
  h2 {
    font-size: 30px;
    text-align: center;
  }
  span {
    font-size: 24px;
  }
`;
export default DetailTimeStamp;
