import { styled } from "styled-components";
import { Spacer } from "../Spacer";
import { formatBidPriceByComma } from "../../../common/formatUtil";
import { useState } from "react";

const BidCustomModal = () => {
  const [bidPriceState, setBidPriceState] = useState<string>("0");
  const onChangePriceHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
    const formatPrice = formatBidPriceByComma(e.target.value);
    setBidPriceState(formatPrice);
  };

  return (
    <StCustomModalWrapper>
      <StCustomModalContentWrapper>
        <div>
          <h1>현재 최고 입찰가</h1>
          <span> ₩ 1,100,000</span>
        </div>
        <Spacer y={70} />
        <StBidForm>
          <label htmlFor="bid_price">
            <input type="text" id="bid_price" onChange={onChangePriceHandler} />
          </label>
          <Spacer y={40} />
          <button>입찰하기</button>
        </StBidForm>
        <StCloseButton>X</StCloseButton>
      </StCustomModalContentWrapper>
    </StCustomModalWrapper>
  );
};
const StCustomModalWrapper = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  margin: auto;
  position: fixed;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 10;
`;

const StCustomModalContentWrapper = styled.div`
  position: relative;
  width: 600px;
  height: 400px;
  margin: auto;
  border-radius: 15px;
  background-color: white;
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  > div {
    display: flex;
    justify-content: center;
    gap: 10px;
    > h1 {
      display: inline-block;
    }
    > h1,
    span {
      font-weight: bold;
      font-size: 24px;
    }
    > span {
      font-size: 30px;
    }
  }
`;

const StBidForm = styled.form`
  display: flex;
  flex-direction: column;
  margin: 0 auto;
  max-width: 400px;
  width: 100%;
  > label {
    width: 100%;
    position: relative;
    &::before {
      content: "₩ ";
      position: absolute;
      top: 50%;
      transform: translateY(-45%);
      left: 10px;
      z-index: 200;
      font-size: 24px;
      font-weight: bold;
    }
  }
  > label > input {
    outline: none;
    padding: 10px 0;
    width: 100%;
    text-align: right;
    font-size: 24px;
  }
  > button {
    outline: none;
    padding: 10px 0;
    background-color: black;
    color: white;
    border: unset;
    border-radius: 5px;
    cursor: pointer;
  }
`;

const StCloseButton = styled.button`
  position: absolute;
  cursor: pointer;
  background-color: unset;
  border: unset;
  top: 10px;
  right: 10px;
  height: 20px;
`;

export default BidCustomModal;
