import { useParams } from "react-router-dom";
import styled from "styled-components";
import { useCustomQuery } from "../hooks/useCustomQuery";
import { fetchGetAuctionById } from "../api/auction";
import { Auction_post } from "../types/databaseRetrunTypes";
import placeholder from "../images/placeholder.svg";
import DetailInfo from "../components/detail/DetailInfo";
import DetailContent from "../components/detail/DetailContent";
import { useEffect, useState } from "react";
import { calculateTimeUntil } from "../common/dayjs";

const Detail = () => {
  const { auctionId } = useParams();
  const [auctionTimeStamp, setAuctionTimeStamp] = useState<string>();
  const [auctionOver, setAuctionOver] = useState<boolean>(false);
  const queryOptions = {
    queryKey: ["getAuction"],
    queryFn: () => fetchGetAuctionById(auctionId!),
    queryOptions: { staleTime: Infinity },
  };
  const data = useCustomQuery<Auction_post, Error>(queryOptions);
  const thumbnailImg = data?.auction_images?.[0]?.image_path ?? placeholder;

  useEffect(() => {
    setInterval(() => {
      const calculateResult = calculateTimeUntil(data?.auction_end_date!);
      setAuctionTimeStamp(calculateResult.message);
      setAuctionOver(calculateResult.isOver);
    }, 1000);
  }, [data]);

  return (
    <StDetailWrapper>
      <StDetailInfo>
        <StDetailImgWrapper>
          <img src={thumbnailImg} alt={data?.title} />
        </StDetailImgWrapper>
        <DetailInfo auctionData={data} />
      </StDetailInfo>
      <DetailContent auctionContent={data?.content} />
      <div>
        {auctionOver && <div>경매가 종료되었습니다.</div>}
        {!auctionOver && (
          <div>
            <h2>경매 남은시간</h2>
            <span>{auctionTimeStamp}</span>
          </div>
        )}
      </div>
    </StDetailWrapper>
  );
};

const StDetailWrapper = styled.main`
  max-width: 1200px;
  width: 100%;
  margin: 100px auto 0;
`;

const StDetailInfo = styled.section`
  display: flex;
  column-gap: 45px;
  margin: 0 auto;
  justify-content: center;
`;

const StDetailImgWrapper = styled.div`
  max-width: 300px;
  > img {
    width: 100%;
  }
`;

export default Detail;
