import { Auction_post } from "../types/databaseRetrunTypes";
import { calculateTimeUntil } from "../common/dayjs";
import { useEffect, useState } from "react";
import { AuctionStatus } from "../types/detailTyps";

type Parameter =
  | Pick<Auction_post, "auction_status" | "auction_end_date">
  | undefined;
const useAuctionStatus = (data: Parameter) => {
  const [auctionTimeStamp, setAuctionTimeStamp] = useState<string>();
  const [auctionOver, setAuctionOver] = useState<boolean>(false);

  useEffect(() => {
    // 경매품의 상태 관리
    if (Number(data?.auction_status) === AuctionStatus.READY) {
      setAuctionTimeStamp("경매 시작 전입니다.");
    } else if (Number(data?.auction_status) === AuctionStatus.START) {
      setInterval(() => {
        const calculateResult = calculateTimeUntil(data?.auction_end_date!);
        setAuctionTimeStamp(calculateResult.message);
        setAuctionOver(calculateResult.isOver);
      }, 1000);
    } else if (Number(data?.auction_status) === AuctionStatus.END) {
      setAuctionTimeStamp("경매가 종료되었습니다.");
      setAuctionOver(true);
    }
  }, [data?.auction_status]);

  return { auctionTimeStamp, auctionOver };
};
export default useAuctionStatus;
