import { useQuery } from "@tanstack/react-query";
import { List } from "antd";
import { useEffect, useState } from "react";
import { styled } from "styled-components";
import { fetchGetAuctions } from "../../../api/auction";
import { supabase } from "../../../supabase";
import { Auction_post, Category } from "../../../types/databaseRetrunTypes";
import ListSkeleton from "../../ListSkeletom/ListSkeleton";
import PostItem from "./PostItem/PostItem";

const PostList = () => {
  const [userId, setUserId] = useState<string>("");

  useEffect(() => {
    const fetchUser = async () => {
      const { data } = await supabase.auth.getUser();
      setUserId(data.user?.id as string);
    };
    fetchUser();
  }, []);

  const queryOption = {
    searchKeyword: "",
    categories: [] as Pick<Category, "category_id">[],
    limit: 20,
    offset: 0,
    orderBy: "created_at",
    order: false,
    user_id: userId,
  };

  const {
    data: posts,
    isLoading,
    isError,
    error,
  } = useQuery<Auction_post[]>({
    queryKey: ["posts"],
    queryFn: () =>
      fetchGetAuctions(
        queryOption.searchKeyword,
        queryOption.categories,
        queryOption.limit,
        queryOption.offset,
        queryOption.orderBy,
        queryOption.order,
        queryOption.user_id
      ),
  });

  if (isLoading) {
    return <ListSkeleton />;
  }

  return (
    <StPostListWrapper>
      <List
        itemLayout="vertical"
        size="large"
        pagination={{
          onChange: (page) => {
            console.log(page);
          },
          pageSize: 3,
        }}
        dataSource={posts}
        footer={
          <div>
            <b>ant design</b> footer part
          </div>
        }
      />

      {posts?.map((post) => <PostItem post={post} />)}
    </StPostListWrapper>
  );
};

const StPostListWrapper = styled.ul`
  display: flex;
  flex-direction: column;
  padding: 1rem;
  gap: 1rem;
`;

const StSkeleton = styled.div`
  display: flex;
  flex-direction: column;
`;

export default PostList;
